StructureSpawn.prototype.support_creep=function(a){this.memory.supported_creeps||(this.memory.supported_creeps=[]);this.memory.supported_creeps.push(a.name);a.memory.support=this.name};StructureSpawn.prototype.unsupport_creep=function(a){this.memory.supported_creeps||(this.memory.supported_creeps=[]);_.remove(this.memory.supported_creeps,function(b){return b==a.name});a.memory.support==this.name&&(a.memory.support=null)};
StructureSpawn.prototype.check_creeps=function(){this.memory.supported_creeps||(this.memory.supported_creeps=[]);_.remove(this.memory.supported_creeps,function(a,b,c){return!Game.creeps[a]})};
